| stories | CREATE TABLE `stories` (
  `id` mediumint(8) unsigned NOT NULL AUTO_INCREMENT,
  `user_id` mediumint(8) unsigned NOT NULL,
  `story_title` varchar(255) NOT NULL,
  `story_link` varchar(255) DEFAULT NULL,
  `story_body` text NOT NULL,
  `update_time` datetime DEFAULT current_timestamp() ON UPDATE current_timestamp(),
  `upvotes` mediumint(8) unsigned DEFAULT 0,
  `downvotes` mediumint(8) unsigned DEFAULT 0,
  PRIMARY KEY (`id`),
  KEY `user_id` (`user_id`),
  CONSTRAINT `stories_ibfk_1` FOREIGN KEY (`user_id`) REFERENCES `users` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=utf8 |
